import React, { Component } from 'react';

class Search extends Component {

  constructor(props) {
    super(props);
    this.state = {
        quote: '',
        data:''
      };
}

componentDidMount() {
    console.log("nnnnn",this.props)
  const data =[...this.props.data];
      this.setState({data},()=>{console.log("hhhhe",this.state.data)});
}
  findQuote = ( e) => {
    e.preventDefault();

  }

  onChange = e => {
      const value= e.target.value;
    this.setState({ quote: value });
  };

  render() {
      console.log("ser",this.state.data)
    //   if(!this.state || !this.state.data) {
    //     console.log("ser",this.state.data)
    //       return <div>Loading...</div>

    //   }
    return (
            <div>
              <form onSubmit={this.findQuote.bind(this)}>
                <div>
                  <input
                    type="text"
                    name="quote"
                    placeholder="quote"
                    value={this.state.quote}
                    onChange={this.onChange}
                  />
                </div>
                <button
                  type="submit"
                >
                  Search
                </button>
              </form>
              <table>
                <tbody>{this.state.data.map(function(item, key) {
             if(item.quote.symbol== this.state.quote) {
                 return (

                    <tr key = {key}>
                        <td>{item.quote.change}</td>
                        <td>{item.id}</td>
                        <td>{item.title}</td>
                        <td>{item.body}</td>
                    </tr>
                  )

             }
             else 
             return null;             
             })}</tbody>
       </table>
            </div>
          );


  }
}

export default Search;
